import javax.persistence.EntityManager;

import org.junit.Before;
import org.junit.Test;

import poolingpeople.webapplication.business.user.boundary.UserManager;
import poolingpeople.webapplication.business.user.entity.Users;

public class UserManagerTest {

	UserManager manager;
	@Mock Users usersMock;
	@Mock EntityManager entityManagerMock;

	@Before
	public void setUp() throws Exception {
		MockitoAnnotations.initMocks(this);
		manager = new UserManager();
		manager.entityManager = entityManagerMock;
	}

	@Test
	public void testRemoveUser() {
		manager.removeUser(usersMock);
		verify(entityManagerMock, times(1)).remove(usersMock);
	}

	@Test
	public void testGetUserById() {
		when(entityManagerMock.find(Users.class, 33L)).thenReturn(usersMock);
		assertNotNull(manager.getUserById(33L));
		verify(entityManagerMock, times(1)).find(Users.class, 33L);
	}

	@Test
	public void testPersistNewUser() {
		when(usersMock.getId()).thenReturn(0L);
		assertNotNull(manager.persistUser(usersMock));
		verify(entityManagerMock,times(1)).persist(usersMock);
	}

	@Test
	public void testMergeOldUser() {
		when(usersMock.getId()).thenReturn(48L);
		assertNotNull(manager.persistUser(usersMock));
		verify(entityManagerMock,times(1)).merge(usersMock);
	}

}

