package poolingpeople.webapplication.business.logging.boundary;

import java.util.logging.Logger;

import javax.inject.Inject;
import javax.interceptor.AroundInvoke;
import javax.interceptor.Interceptor;
import javax.interceptor.InvocationContext;

import poolingpeople.webapplication.business.configuration.boundary.Configurable;

@Interceptor
@Loggable
public class LoggerInterceptor {
	@Inject
	private Logger logger;
	@Inject @Configurable("isLogging")
	private boolean isLogginOn;

	@AroundInvoke
	public Object logMethod(InvocationContext context) throws Exception {
		return (isLogginOn) ? logMethodInvocation(context) : noLogging(context) ;
	}

	private Object noLogging(InvocationContext context) throws Exception {
		return context.proceed();
	}

	private Object logMethodInvocation(InvocationContext context)
			throws Exception {
		logger.info("Entering: " +
				context.getMethod().getDeclaringClass().getCanonicalName()+"#"+ 
				context.getMethod().getName() + stringifyParameters(context.getParameters()));
		try {
			return context.proceed();
		} finally {
			logger.info("Exiting: "+context.getMethod().getDeclaringClass().getCanonicalName()+"#"+ context.getMethod().getName());
		}
	}

	private String stringifyParameters(Object[] params) {

		String stringifiedParams = "";

		if (params != null) {
			for(Object param : params) {
				if(param == null) continue;
				stringifiedParams += ":" + param.toString();
			}
		}
		return stringifiedParams; 
	}
}

package poolingpeople.webapplication.business.user.boundary;

import java.util.List;

import javax.ejb.Stateless;
import javax.persistence.EntityManager;
import javax.persistence.NamedQueries;
import javax.persistence.NamedQuery;
import javax.persistence.PersistenceContext;
import javax.ws.rs.Consumes;
import javax.ws.rs.GET;
import javax.ws.rs.PUT;
import javax.ws.rs.Path;
import javax.ws.rs.PathParam;
import javax.ws.rs.Produces;
import javax.ws.rs.WebApplicationException;
import javax.ws.rs.core.MediaType;
import javax.ws.rs.core.Response;

import org.codehaus.jackson.map.ObjectMapper;

import poolingpeople.webapplication.business.logging.boundary.Loggable;
import poolingpeople.webapplication.business.user.entity.Users;

@Path("hello")
@Stateless
@Loggable
@NamedQueries({
@NamedQuery(name="s",
        query="SELECT v from Users v")          
})
public class Hello {
	ObjectMapper mapper = new ObjectMapper();

	@PersistenceContext 
	EntityManager manager;
	@SuppressWarnings("unchecked")
	@Produces(MediaType.APPLICATION_JSON)
	@GET
	public String getAllUsers() {
		try {
			List&lt;Users&gt; l = manager.createQuery("select d from Users d").getResultList();
			return mapper.writeValueAsString(l);
		} catch(Exception e) {
			throw new WebApplicationException(Response.serverError().entity("some error server").build());
		}
	}

	@GET
	@Path("{id:[0-9]+}")
	@Produces(MediaType.APPLICATION_JSON)
	public String getUser(@PathParam("id") Long  id) {
		 Object u = (Object) manager.createQuery("select d from Users d where d.id=:id")
				 .setParameter("id" ,id)
				 .getSingleResult();

		 try {
			return mapper.writeValueAsString(u);
		} catch(Exception e) {
			throw new WebApplicationException(Response.serverError().entity("some error server").build());
		}
	}

	@PUT
	@Consumes(MediaType.APPLICATION_JSON)
	@Path("new/user")
	public String addUser(String user) {
		try {
			Users users = mapper.readValue(user, Users.class);
			manager.persist(users);
			return mapper.writeValueAsString(users.getId());
		} catch(Exception e) {
			throw new WebApplicationException(Response.serverError().entity("some error server").build());
		}
	}
}

